mapper.select:
select movie_id as movieId, movie_name as movieName
  from movies
 where flag = #{flag}

mapper.update:
update box_order
   set audience = #{audience},
       audience_date = #{audienceDate}
 where movie_id = #{movieId}

mapper.select:
select genre
  from genres
 where movie_id = #{movieId}

select name, route
  from videos
 where movie_id = #{movieId}

select route
  from photos
 where movie_id = #{movieId}
